INSERT INTO `config_info` VALUES (5, 'bizspring-upms-dev.yml', 'DEFAULT_GROUP', '## spring security 配置\nsecurity:\n  oauth2:\n    client:\n      client-id: admin\n      client-secret: admin\n      scope: server\n      # 无需token访问的url\n      release-urls:\n        - /member/**\n        - /merchant/**\n        - /actuator/**\n        - /v2/api-docs\n        - /user/register\n        - /api/**\n        - /druid/**\n# 数据源\nspring:\n  datasource:\n    type: com.alibaba.druid.pool.DruidDataSource\n    druid:\n      driver-class-name: com.mysql.cj.jdbc.Driver\n      use-ping-method: false\n      username: root\n      password: root      \n      url: jdbc:mysql://bizspring-mysql:3306/bizspring_open_upms?characterEncoding=utf8&zeroDateTimeBehavior=convertToNull&useSSL=false&useJDBCCompliantTimezoneShift=true&useLegacyDatetimeCode=false&serverTimezone=GMT%2B8&allowMultiQueries=true&allowPublicKeyRetrieval=true\n      web-stat-filter: \n        enabled: true\n      stat-view-servlet:\n        enabled: true\n        # 设置白名单，不填则允许所有访问\n        allow: \n        url-pattern: /druid/*\n        # 控制台管理用户名和密码\n        login-username: admin\n        login-password: 123456\n      filter:\n        stat:\n            enabled: true\n            # 慢SQL记录\n            log-slow-sql: true\n            slow-sql-millis: 1000\n            merge-sql: true\n        wall:\n            config:\n                multi-statement-allow: true\n# Logger Config sql日志\nlogging:\n  level:\n    cn.bizspring.cloud.upms.admin.mapper: debug  \nbase:\n  # 租户表维护\n  tenant:\n    column: tenant_id\n    tables: \n      # - biz_users\n      # - biz_roles\n      # - biz_oauth_client\n      # - biz_organ\n      # - biz_config_storage\n      # - biz_organ_relation\n      # - biz_role_menu\n      # - biz_user_role\n     \n  #数据权限配置\n  datascope:\n    mapperIds:\n      - cn.bizspring.cloud.upms.admin.mapper.UserMapper.getUserVosPage        \n#邮箱配置\nemail:\n  mailSmtpHost: smtpdm.aliyun.com\n  mailSmtpUsername: official@email.bizspring.cn\n  mailSmtpPassword: BizSpring12345678\n  siteName: BizSpring\n#阿里短信配置\nsms:\n  regionId: cn-hangzhou\n  accessKeyId: xxxxxxxxx\n  accessKeySecret: xxxxxxxxxxxxxxxxxx\n  #模板\n  templates:\n    #登录模板\n    signName1: bizspring商城\n    templateCode1: SMS_183247308\n    #绑定模板\n    signName2: bizspring商城\n    templateCode2: SMS_183247308\n    #解绑模板\n    signName3: bizspring\n    templateCode3: SMS_183247308\n\nshowStoreCenter: true\nredisCacheUrl: 127.0.0.1:6381\n', '5c36654f2fd70cdd28b338d31af70bb7', '2019-07-28 23:14:26', '2024-09-21 19:21:49', NULL, '127.0.0.1', '', '', '用户权限管理配置', 'null', 'null', 'yaml', 'null');
