INSERT INTO `his_config_info` VALUES (2, 422, 'application-dev.yml', 'DEFAULT_GROUP', '', '# 加解密根密码\njasypt:\n  encryptor:\n    #根密码，改完密码要把bizspring_upms.sys_datasource数据库表清空，否则代码生成器无法启动\n    password: bizspring\nspring:\n  # main:\n    #允许存在多个Feign调用相同Service的接口\n  #  allow-bean-definition-overriding: true\n  servlet:\n    multipart:\n      location: /home\n      maxFileSize: 30MB\n      maxRequestSize: 30MB\n  # redis 相关\n  redis:\n    # 单机 --->\n    # host: bizspring-redis\n    # port: 6379\n    # 单机 --->\n  \n  # 集群\n    cluster:\n      # 各 Redis 节点信息\n      nodes: bizspring-redis:6379,bizspring-redis:6380,bizspring-redis:6381,bizspring-redis:6382,bizspring-redis:6383,bizspring-redis:6384\n      maxRedirects: 3\n  # 集群\n    database: 0\n    lettuce:\n      pool:\n        # 连接池最大连接数 默认8 ，负数表示没有限制\n        max-active: 8\n        # 连接池最大阻塞等待时间（使用负值表示没有限制） 默认-1\n        max-wait: -1\n        # 连接池中的最大空闲连接 默认8\n        max-idle: 8\n        # 连接池中的最小空闲连接 默认0\n        min-idle: 0\n\n    # 集群\n# ====================================================================================\n# spring-redis-redisson\n# ====================================================================================\n    redisson:\n      pool:\n        # 连接池最大连接数 默认8 ，负数表示没有限制\n        max-active: 8\n        # 连接池最大阻塞等待时间（使用负值表示没有限制） 默认-1\n        max-wait: -1\n        # 连接池中的最大空闲连接 默认8\n        max-idle: 8\n        # 连接池中的最小空闲连接 默认0\n        min-idle: 0\n        connTimeout: 10000\n        timeout: 3000\n        soTimeout: 3000\n        size: 32\n\n      config: | \n        clusterServersConfig:\n          idleConnectionTimeout: 10000\n          connectTimeout: 10000\n          timeout: 3000\n          retryAttempts: 3\n          retryInterval: 1500\n          failedSlaveReconnectionInterval: 3000\n          failedSlaveCheckInterval: 60000\n          password: null\n          subscriptionsPerConnection: 5\n          clientName: null\n          loadBalancer: !<org.redisson.connection.balancer.RoundRobinLoadBalancer> {}\n          subscriptionConnectionMinimumIdleSize: 1\n          subscriptionConnectionPoolSize: 50\n          slaveConnectionMinimumIdleSize: 24\n          slaveConnectionPoolSize: 64\n          masterConnectionMinimumIdleSize: 24\n          masterConnectionPoolSize: 64\n          readMode: \"SLAVE\"\n          subscriptionMode: \"SLAVE\"\n          nodeAddresses:\n          - \"redis://bizspring-redis:6379\"\n          - \"redis://bizspring-redis:6380\"\n          - \"redis://bizspring-redis:6381\"\n          - \"redis://bizspring-redis:6382\"\n          - \"redis://bizspring-redis:6383\"\n          - \"redis://bizspring-redis:6384\"\n          scanInterval: 1000\n          pingConnectionInterval: 0\n          keepAlive: false\n          tcpNoDelay: false\n        threads: 16\n        nettyThreads: 32\n        codec: !<org.redisson.codec.MarshallingCodec> {}\n        transportMode: \"NIO\"\n\n    # 单机\n    # singleServerConfig:\n    #     password:\n    #     timeout: 10000\n    #     address: redis://127.0.0.1:6379\n  \n  elasticsearch:\n    # es相关配置 Connecting to Elasticsearch using REST clients\n    rest:\n      uris: http://localhost:9200\n      connection-timeout: 100s\n      read-timeout: 100s\n      # username: elastic\n      # password: 123456\n    # es相关配置 Connecting to Elasticsearch using Jest clients\n    # jest:\n    #   uris: http://192.168.8.202:9200\n    #   connection-timeout: 10s\n    #   read-timeout: 10s\n    #   username: elastic\n    #   password: 123456\n   \n            \n# logging日志\nlogging:\n  level:\n    com.alibaba.nacos.client.naming: error\n    com.lucifer.springboot.cache.mapper: debug\n# 暴露监控端点\nmanagement:\n  endpoints:\n    web:\n      exposure:\n        include: \'*\'\n# 关闭es健康检查\n  health:\n    elasticsearch:\n      enabled: false\n# feign 配置\nfeign:\n  sentinel:\n    enabled: true\n  okhttp:\n    enabled: true\n  httpclient:\n    enabled: false\n  client:\n    config:\n      default:\n        connectTimeout: 100000\n        readTimeout: 100000\n  compression:\n    request:\n      enabled: true\n    response:\n      enabled: true\n#请求处理的超时时间\nribbon:\n  ReadTimeout: 10000000\n  ConnectTimeout: 10000000\n# mybaits-plus配置\nmybatis-plus:\n  # MyBatis Mapper所对应的XML文件位置\n  mapper-locations: classpath:/mapper/*Mapper.xml\n  type-enums-package: cn.bizspring.cloud.*.common.entity\n  # 自定义TypeHandler\n  type-handlers-package: cn.bizspring.cloud.common.data.mybatis.typehandler\n  global-config:\n    sql-parser-cache: true\n    # 关闭MP3.0自带的banner\n    banner: false\n    db-config:\n      # 主键类型\n      id-type: auto\n#swagger公共信息\nswagger:\n  title:  API\n  description: bizspring\n  license: Powered By bizspring\n  licenseUrl: http://www.bizspring.cn/\n  terms-of-service-url: http://www.bizspring.cn/\n  authorization:\n    name: OAuth\n    auth-regex: ^.*$\n    authorization-scope-list:\n      - scope: server\n        description: server all\n    token-url-list:\n      - http://localhost:9979/auth/oauth/token\n\n## spring security 配置\nsecurity:\n  oauth2:\n    client:\n      # 无需token访问的url,如果子模块重写这里的配置就会被覆盖\n      release-urls:\n        - /actuator/**\n        - /v2/api-docs\n    resource:\n      loadBalanced: true\n      token-info-uri: http://bizspring-auth/oauth/check_token\n## 文件存放目录配置（用来存放微信支付证书）\nrootpath:\n  windows: C:/bizspring-file/\n  linux: /mnt/install/bizspring-file/\n', 'c54542918485831faf04f51337f12103', '2024-09-21 19:55:42', '2024-09-21 19:55:42', NULL, '127.0.0.1', 'U', '');

